/* eslint-disable react/jsx-props-no-spreading */
import React, { useEffect, useMemo, useState } from 'react';
import { useForm } from 'react-hook-form';
import * as PropTypes from 'prop-types';
import { useNavigate } from 'react-router-dom';
import { generateStarsInputs } from '../../../lib/functions';
import { useFilePreview } from '../../../lib/customHooks';
import addFileIMG from '../../../images/add_file.png';
import styles from './BookForm.module.css';
import { updateBook, addBook } from '../../../lib/common';
const API_URL = process.env.REACT_APP_API_URL;

function BookForm({ book, validate }) {
  const userRating = book ? book.ratings.find((elt) => elt.userId === localStorage.getItem('userId'))?.grade : 0;

  const [rating, setRating] = useState(0);

  const navigate = useNavigate();
  const {
    register, watch, formState, handleSubmit, reset,
  } = useForm({
    defaultValues: useMemo(() => ({
      title: book?.title,
      author: book?.author,
      year: book?.year,
      genre: book?.genre,
    }), [book]),
  });
  useEffect(() => {
    reset(book);
  }, [book]);
  const file = watch(['file']);
  const [filePreview] = useFilePreview(file);

  useEffect(() => {
    setRating(userRating);
  }, [userRating]);

  useEffect(() => {
    if (!book && formState.dirtyFields.rating) {
      const rate = document.querySelector('input[name="rating"]:checked').value;
      setRating(parseInt(rate, 10));
      formState.dirtyFields.rating = false;
    }
  }, [formState]);

  const onSubmit = async (data) => {
    // When we create a new book
    if (!book) {
      if (!data.file[0]) {
        alert('Vous devez ajouter une image');
      }
      if (!data.rating) {
        /* eslint-disable no-param-reassign */
        data.rating = 0;
        /* eslint-enable no-param-reassign */
      }
      const newBook = await addBook(data);
      if (!newBook.error) {
        validate(true);
      } else {
        alert(newBook.message);
      }
    } else {
      const updatedBook = await updateBook(data, data.id);
      if (!updatedBook.error) {
        navigate('/');
      } else {
        alert(updatedBook.message);
      }
    }
  };

  const readOnlyStars = !!book;
  return (
    <form onSubmit={handleSubmit(onSubmit)} className={styles.Form}>
      <input type="hidden" id="id" {...register('id')} />
      <label htmlFor="title">
        <p>Titre du livre</p>
        <input
          type="text"
          id="title"
          {...register('title', { required: 'Le titre est requis' })}
        />
        {formState.errors.title && <p className={styles.error}>{formState.errors.title.message}</p>}
      </label>
      <label htmlFor="author">
        <p>Auteur</p>
        <input
          type="text"
          id="author"
          {...register('author', { required: "L'auteur est requis" })}
        />
        {formState.errors.author
        && <p className={styles.error}>{formState.errors.author.message}</p>}
      </label>
      <label htmlFor="year">
        <p>Année de publication</p>
        <input
          type="text"
          id="year"
          {
            ...register('year', {
              required: 'L\'année de publication est requise',
              validate: (value) => {
                const currentYear = new Date().getFullYear();
                const yearNumber = parseInt(value, 10);
                if (Number.isNaN(yearNumber)) {
                  return 'L\'année doit être un nombre.';
                }
                if (yearNumber < 0 || yearNumber > currentYear) {
                  return `L'année doit être comprise entre 1 et ${currentYear}.`;
                }
                return true; // Validation réussie
              },
            })
          }
        />
        {formState.errors.year && <p className={styles.error}>{formState.errors.year.message}</p>}
      </label>
      <label htmlFor="genre">
        <p>Genre</p>
        <input
          type="text"
          id="genre"
          {...register('genre', { required: 'Le genre est requis' })}
        />
        {formState.errors.genre && <p className={styles.error}>{formState.errors.genre.message}</p>}
      </label>
      <label htmlFor="rate">
        <p>Note</p>
        <div className={styles.Stars}>
          {generateStarsInputs(rating, register, readOnlyStars)}
        </div>
      </label>
      <label htmlFor="file">
        <p>Visuel</p>
        <div className={styles.AddImage}>
          {filePreview || book?.imageUrl ? (
            <>
              <img src={filePreview ?? (book?.imageUrl ? `${API_URL}${book.imageUrl}` : '')} alt="preview" />
              <p>Modifier</p>
            </>
          ) : (
            <>
              <img src={addFileIMG} alt="Add file" />
              <p>Ajouter une image</p>
            </>
          )}

        </div>
        <input {...register('file')} type="file" id="file" />
      </label>
      <button type="submit">Publier</button>
    </form>
  );
}

BookForm.propTypes = {
  book: PropTypes.shape({
    id: PropTypes.string,
    _id: PropTypes.string,
    userId: PropTypes.string,
    title: PropTypes.string,
    author: PropTypes.string,
    year: PropTypes.number,
    imageUrl: PropTypes.string,
    genre: PropTypes.string,
    ratings: PropTypes.arrayOf(PropTypes.shape({
      userId: PropTypes.string,
      grade: PropTypes.number,
    })),
    averageRating: PropTypes.number,
  }),
  validate: PropTypes.func,
};

BookForm.defaultProps = {
  book: null,
  validate: null,
};
export default BookForm;
